package cshirt2

import (
	"crypto/rand"
	"math/big"
)

var dhGroup5 = func() *big.Int {
	toret := big.NewInt(0)
	toret.SetString("", 16)
	return toret
}()

type pubKey []byte
type secKey []byte

func udhSecret(lsk secKey, rpk pubKey) []byte {
	bitlen := len(lsk) * 8
	// checks
	if bitlen != 1536 {
		panic("Why are you trying to generate DH key with wrong bitlen?")
	}
	var group *big.Int
	group = dhGroup5
	return big.NewInt(0).Exp(big.NewInt(0).SetBytes(rpk),
		big.NewInt(0).SetBytes(lsk), group).Bytes()
}

func dhGenKey() (pk pubKey, sk secKey) {
	const bitlen = 1536
	var group *big.Int
	group = dhGroup5
	// randomly generate even private key
	pub := pubKey(make([]byte, bitlen/8))
	priv := secKey(make([]byte, bitlen/8))
	rand.Read(priv)
	priv[bitlen/8-1] /= 2
	priv[bitlen/8-1] *= 2
	privBnum := big.NewInt(0).SetBytes(priv)
retry:
	// generate public key
	pubBnum := big.NewInt(0).Exp(big.NewInt(2), privBnum, group)
	ggg := make([]byte, 1)
	rand.Read(ggg)
	if ggg[0]%2 == 0 {
		pubBnum = big.NewInt(0).Sub(group, pubBnum)
	}
	// Obtain pubkey
	candid := pubBnum.Bytes()
	if len(candid) != len(pub) {
		goto retry
	}
	copy(pub, candid)
	globCacheLock.Lock()
	defer globCacheLock.Unlock()
	globCache.SetDefault(string(pk), false)
	return pub, priv
}
